class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])

        p1, p2, p3, p4 = [0, 0], [0, m - 1], [n - 1, 0], [n - 1, m - 1]
        res = 0
        c2 = 0
        c1 = 0
        c0 = 0

        def get(p):
            x, y = p
            return grid[x][y]

        while p1[0] < p3[0]:
            while p1[1] < p2[1]:
                temp = [get(p1), get(p2), get(p3), get(p4)]
                c = temp.count(1)
                res += min(c, 4 - c)

                p1 = [p1[0], p1[1] + 1]
                p2 = [p2[0], p2[1] - 1]
                p3 = [p3[0], p3[1] + 1]
                p4 = [p4[0], p4[1] - 1]

            if p1[1] == p2[1]:
                # print(p1, p2, p3, p4, get(p1), get(p3), grid)
                if get(p1) != get(p3):
                    c2 += 1
                elif get(p1) == 1:
                    c1 += 2

            p1 = [p1[0] + 1, 0]
            p2 = [p2[0] + 1, m - 1]
            p3 = [p3[0] - 1, 0]
            p4 = [p4[0] - 1, m - 1]

        if p1[0] == p3[0]:
            while p1[1] < p2[1]:
                if get(p1) != get(p2):
                    c2 += 1
                elif get(p1) == 1:
                    c1 += 2

                p1 = [p1[0], p1[1] + 1]
                p2 = [p2[0], p2[1] - 1]

            if p1[1] == p2[1]:
                if get(p1) == 1:
                    res += 1

        if c1%4 > 0:
            if c2 >= 1:
                res += c2
            else:
                res += 2
        else:
            res += c2

        return res
