class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int ans = 0;
        int i1 = 0, i2 = n-1;
        while(i1<i2){
            int j1 = 0, j2 = m-1;
            while(j1<j2){
                int c0 = a[i1][j1] + a[i1][j2] + a[i2][j1] + a[i2][j2];
                ans += min(c0,4-c0);
                j1++;j2--;
            }
            i1++;i2--;
        }

        int flip = 0, one = 0;
        if(n%2){
            int j1 = 0, j2 = m-1;
            while(j1<j2){
                if(a[n/2][j1] != a[n/2][j2]){
                    flip++;
                }
                else if(a[n/2][j1] == 1) one+=2;
                j1++;j2--;
            }
        }

        if(m%2){
            int j1 = 0, j2 = n-1;
            while(j1<j2){
                if(a[j1][m/2] != a[j2][m/2]){
                    flip++;
                }
                else if(a[j1][m/2] == 1) one+=2;
                j1++;j2--;
            }
        }

        // cout<<"one "<<one<<endl;


        if(flip){
            if(one%4==0){
                ans+=flip;
            }
            else{
                ans+=flip;
            }
        }
        else{
            if(one%4==0) ans+=0;
            else ans+=2;
        }

        if(n%2 && m%2){
            ans += a[n/2][m/2];
        }

        return ans;
    }
};
