class Solution {
    public int minFlips(int[][] a) {
        int m = a.length;
        int n = a[0].length;
        int ans = 0;
        for (int i = 0; i < m/2; i++) {
            for (int j = 0; j < n/2; j++) {
                int count = 0;
                if (a[i][j] == 1) count++;
                if (a[i][n-1-j] == 1) count++;
                if (a[m-1-i][j] == 1) count++;
                if (a[m-1-i][n-1-j] == 1) count++;
                if (count == 2) ans += 2;
                else if (count == 1 || count == 3) ans += 1;
            }
        }

        if (m % 2 == 1 && n % 2 == 1) {
            if (a[m/2][n/2] == 1) ans++;
        }

        int single = 0;
        int pair = 0;
        if (m % 2 == 1) {
            for (int j = 0; j < n/2; j++) {
                if (a[m/2][j] == 0) {
                    if (a[m/2][n-1-j] == 1) single++;
                } else {
                    if (a[m/2][n-1-j] == 1) pair++;
                    else single++;
                }
            }
        }
        if (n % 2 == 1) {
            for (int i = 0; i < m/2; i++) {
                if (a[i][n/2] == 0) {
                    if (a[m-1-i][n/2] == 1) single++;
                } else {
                    if (a[m-1-i][n/2] == 1) pair++;
                    else single++;
                }
            }
        }
        pair = (pair*2) % 4;
        ans += single;
        if (pair == 2) {
            if (single == 0) ans += 2;
        }
        return ans;
    }
}
