int minFlips(int** grid, int m, int* del) {
    const int n=del[0];
    int sum=0;
    int paircount[3]={0,0,0};
    for(int up=0,down=m-1;up<=down;up++,down--){
        for(int left=0,right=n-1;left<=right;left++,right--){
            int type=(up!=down)+(left!=right);
            int count=grid[up][left]+grid[up][right]+grid[down][left]+grid[down][right];
            switch(type){
                case 0:if(count)sum++;break;
                case 2:static const int arr[5]={0,1,2,1,0};
                sum+=arr[count];break;
                default:

                paircount[count/2]++;if(count==2)sum++;break;
            }
        }
    }
    if((paircount[1]==0)&&(paircount[2]%2==1))sum+=2;
    return sum;
}
