class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int m=a.size();
        int n=a[0].size();
        int ans=0;
        int n11=0,n10=0,n00=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                set<pair<int,int>> st;
                st.insert({i,j});
                st.insert({i,n-j-1});
                st.insert({m-i-1,j});
                st.insert({m-i-1,n-j-1});
                int ns=st.size();
                if(ns==1){
                    if(a[i][j]==1) ans++;
                    a[i][j]=0;
                }
                else if(ns==4){
                    int n1=0,n0=0;
                    for(auto it:st){
                        if(a[it.first][it.second]==1) n1++;
                        else n0++;
                    }
                    ans+=min(n1,n0);
                    for(auto it:st){
                        a[it.first][it.second]=0;
                    }
                }
                else{
                    int n1=0,n0=0;
                    for(auto it:st){
                        if(a[it.first][it.second]==0) n0++;
                        else n1++;
                        a[it.first][it.second]=0;
                    }
                    if(n1>0 && n0>0) ans++;
                    if(n1==2) n11++;
                    else if(n0==2) n00++;
                    else n10++;
                }
            }
        }
        if(n11%2){
            if(n10==0) ans+=2;
        }
        return ans;
    }
};
