class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n = v.size(),m = v[0].size();
        int cnt = 0,ones = 0;

        int cfp2 = 0,cpp2 = 0;
        for (int i = 0;i < (n+1)/2;i++){
            if ((n&1) && (i+1 == (n+1)/2)){
                for (int j = 0;j < (m+1)/2;j++){
                    if ((m&1) && (j+1 == (m+1)/2)){
                        if (v[i][j] == 1) cnt++;
                    }
                    else{
                        int ones = v[i][j] + v[i][m-1-j];
                        int zeroes = 2-ones;
                        cnt += min(ones,zeroes);
                        if (ones == zeroes) cfp2++;
                        else if (ones == 2) cpp2++;
                    }
                }
            }
            else{
                for (int j = 0;j < (m+1)/2;j++){
                    if ((m&1) && (j+1 == (m+1)/2)){
                        int ones = v[i][j] + v[n-1-i][j];
                        int zeroes = 2-ones;
                        cnt += min(ones,zeroes);
                        if (ones == zeroes) cfp2++;
                        else if (ones == 2) cpp2++;
                    }
                    else{
                        int ones = v[i][j] + v[n-1-i][j] + v[i][m-1-j] + v[n-1-i][m-1-j];
                        int zeroes = 4 - ones;
                        cnt += min(ones,zeroes);
                    }
                }
            }

        }
         if (cpp2%2 == 1 && cfp2 == 0){
            cnt += 2;
        }

        return cnt;

    }
};
