/**
 * @param {number[][]} grid
 * @return {number}
 */
var minFlips = function(grid) {
    const m = grid.length;
    const n = grid[0].length;
    const m2 = m / 2;
    const n2 = n / 2;
    console.log(m2, n2)
    let count = 0; // 要变化的数量
    let zeroOne = 0; // 有 1 和 0 的情况
    let OneOne = 0; // 有 1 和 1 的情况
    for(let i = 0; i < m2; i++) {
        for(let j = 0; j < n2; j++) {
            if (i === m - i - 1 && j === n - j - 1) {
                if (grid[i][j] === 1) { // 最中间的一个数字是 1，要变 0
                    count++;
                }
            } else if (i === m - i - 1) {
                const sum = grid[i][j] + grid[i][n - j - 1];
                if (sum === 1) {
                    count++; // 要变一个数字
                    zeroOne++; // 可以为0也可以为1
                }
                if (sum === 2) {
                    OneOne++; // 两个都是1
                }
            } else if (j === n - j - 1) {
                const sum = grid[i][j] + grid[m - i - 1][j];
                if (sum === 1) {
                    count++; // 要变一个数字
                    zeroOne++; // 可以为0也可以为1
                }
                if (sum === 2) {
                    OneOne++; // 两个都是1
                }
            } else {
                const sum = grid[i][j] + grid[i][n - j - 1] + grid[m - i - 1][j] + grid[m - i - 1][n - j - 1];
                if (sum === 1 || sum === 3) {
                    count++;
                }
                if (sum === 2) {
                    count += 2;
                }
            }
        }
    }

    if (OneOne % 2 !== 0) {
        if (zeroOne === 0) {
            count += 2;
        }
    }

    return count;
};
