class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
       int i,j,n=v.size(),m=v[0].size(),ans=0,ma=0,nm=0;
        //cout<<n<<" "<<m<<endl;
        for(i=0;i<n/2;i++)
        {
            for(j=0;j<m/2;j++)
            {
                int x=v[i][j]+v[n-1-i][j]+v[i][m-1-j]+v[n-1-i][m-1-j];
                ans=ans+min(x,4-x);
            }
        }
        //cout<<ans<<endl;
        if(n%2==1&&m%2==1)
        {
            if(v[n/2][m/2]==1)
                ans++;
            vector<int> a;
            for(i=0;i<n;i++)
                a.push_back(v[i][m/2]);
            i=0,j=a.size()-1,ma=0,nm=0;
            while(i<j)
            {
                if(a[i]==a[j])
                {
                    if(a[i]==1)
                        ma++;
                }
                else 
                    nm++;
                i++;
                j--;
            }
            vector<int> b=v[n/2];
            i=0,j=b.size()-1;
            while(i<j)
            {
                if(b[i]==b[j])
                {
                    if(b[i]==1)
                        ma++;
                }
                else 
                    nm++;
                i++;
                j--;
            }
            ans=ans+nm;
            if(ma%2==1&&nm==0)
            {
                ans=ans+2;
            }
        }
        else if(m%2==1)
        {
            vector<int> a;
            for(i=0;i<n;i++)
                a.push_back(v[i][m/2]);
            i=0,j=a.size()-1,ma=0,nm=0;
            while(i<j)
            {
                if(a[i]==a[j])
                {
                    if(a[i]==1)
                        ma++;
                }
                else 
                    nm++;
                i++;
                j--;
            }
            ans=ans+nm;
            if(ma%2==1&&nm==0)
            {
                ans=ans+2;
            }
        }
        else if(n%2==1)
        {
            vector<int> a=v[n/2];
            i=0,j=a.size()-1,ma=0,nm=0;
            while(i<j)
            {
                if(a[i]==a[j])
                {
                    if(a[i]==1)
                        ma++;
                }
                else 
                    nm++;
                i++;
                j--;
            }
            ans=ans+nm;
            if(ma%2==1&&nm==0)
            {
                ans=ans+2;
            }
        }
        return ans;
    }
};
