class Solution {
public:


int minchanges(vector<vector<int> > mat) 
{ 
    // Rows in the matrix 
    int N = mat.size(); 
    int o = 0,oc=0,k=0;
    // Columns in the matrix 
    int M = mat[0].size(); 

    int i, j, ans = 0, x; 
    map<int, int> mp; 

    for (i = 0; i < N / 2; i++) { 
        for (j = 0; j < M / 2; j++) { 


            mp[(mat[i][M - 1 - j])]++; 
            mp[(mat[i][j])]++; 
            mp[(mat[N - 1 - i][M - 1 - j])]++; 
            mp[(mat[N - 1 - i][j])]++; 

            x = 0; 

            for (auto it = mp.begin(); 
                 it != mp.end(); it++) { 
                x = max(x, it->second); 
                o = it->first;
            } 

            // Min changes to do to make all 
            if(o==1){
                oc+=4;
            }
            ans = ans + 4 - x; 

            // Four elements equal 
            mp.clear(); 
        } 
    } 

    // Make the middle row palindromic 
    if (N % 2 == 1) { 
        for (i = 0; i < M / 2; i++) { 
            if (mat[N / 2][i] 
                != mat[N / 2][M - 1 - i]){
                ans++; 
                k++;
            }else if(mat[N/2][i]==1){
                oc+=2;
            }
        } 
    } 

    if (M % 2 == 1) { 
        for (i = 0; i < N / 2; i++) { 
            if (mat[i][M / 2] 
                != mat[N - 1 - i][M / 2]){ 
                ans++; 
                k++;
                }else if(mat[i][M/2]==1){
                oc+=2;
                }
        } 
    } 
    if(N%2==1 && M%2==1 && mat[N/2][M/2])ans++;
    if(oc%4!=0){
        if(k==0){
            ans+=2;
        }
    }
    return ans;
} 
    int minFlips(vector<vector<int>>& grid) {
        return minchanges(grid);
    }
};
