class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0, n = grid.size() , m = grid[0].size();
        for(int i = 0 ; i < n/2 ; i++){
            for(int j = 0 ; j < m/2 ; j++){
                // (i,j), (n-i-1,j), (i,m-j-1), (n-i-1,m-j-1)
                int sum = grid[i][j]+grid[n-i-1][j]+grid[i][m-j-1]+grid[n-i-1][m-j-1];
                ans += min(sum,4-sum);
            }
        }
        if(n%2 == 0 && m%2 == 0)
            return ans;
        int zerozero = 0, oneone = 0, onezero = 0;
        if(n%2 == 1){
            for(int j = 0 ; j < m/2 ; j++){
                int sum = grid[n/2][j]+grid[n/2][m-j-1];
                if(sum == 0)
                    zerozero++;
                else if(sum == 2)
                    oneone++;
                else
                    onezero++;
            }
        }
        if(m%2 == 1){
            for(int j = 0 ; j < n/2 ; j++){
                int sum = grid[j][m/2]+grid[n-j-1][m/2];
                if(sum == 0)
                    zerozero++;
                else if(sum == 2)
                    oneone++;
                else
                    onezero++;
            }
        }
        if(n%2 == 1 && m%2 == 1 && grid[n/2][m/2])
            ans++;
        if(oneone%2==0){
            return ans+onezero;
        }
        else{
            // make that zero
            int temp1 = onezero;
            int temp2 = onezero;
            int ans1 = ans, ans2 = ans;
            // let that be 11
            ans2 = ans+2;
            ans2 += temp1;
            if(temp1){
                temp1 -= 1;
                ans1 += 1;
                ans1 += temp1;
                return min(ans1,ans2);
            }
            return ans2;
        }
    }
};
