class Solution {
public:
  int minFlips(vector<vector<int>>& a) {
    int n = a.size(), m = a[0].size();
    int res = 0;
    for (int i = 0; i < n / 2; ++i) {
      for (int j = 0; j < m / 2; ++j) {
        int cnt = 0;
        if (a[i][j]) ++cnt;
        if (a[n - i - 1][j]) ++cnt;
        if (a[i][m - j - 1]) ++cnt;
        if (a[n - i - 1][m - j - 1]) ++cnt;
        res += min(cnt, 4 - cnt);
      }
    }

    int cnt = 0;
    int res1 = 0;
    if (n % 2 == 1) {
      for (int j = 0; j < m / 2; ++j) {
        if (a[n / 2][j] != a[n / 2][m - j - 1]) ++res1;
        else if (a[n / 2][j] == 1) ++cnt;
      }
    }

    if (m % 2 == 1) {
      for (int i = 0; i < n / 2; ++i) {
        if (a[i][m / 2] != a[n - i - 1][m / 2]) ++res1;
        else if (a[i][m / 2] == 1) ++cnt;
      }
    }

    res += res1;
    if (cnt % 2 == 1 && res1 == 0) res += 2;
    if (n % 2 == 1 && m % 2 == 1 && a[n / 2][m / 2] == 1) ++res;

    return res;
  }
};
