class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n,m = len(grid), len(grid[0])
        a = 0  
        for i in range(n//2): 
            for j in range(m//2):
                d = grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][m - 1 - j]   
                a += min(d, 4 - d) 

        # print(a)

        choice = False 
        if n%2 and m%2 and grid[n//2][m//2] == 1: 
            a += 1 

        num_ones = 0 

        if m%2: 
            for i in range(n//2): 
                if grid[i][m//2] != grid[n - 1 - i][m//2]: 
                    choice = True 
                    a += 1 
                else: 
                    if grid[i][m//2] == 1: 
                        num_ones += 2 

        # print(a) 

        if n%2: 
            for j in range(m//2): 
                if grid[n//2][j] != grid[n//2][m - 1 - j]: 
                    choice = True 
                    a += 1 
                else: 
                    if grid[n//2][j] == 1: 
                        num_ones += 2 

        if not choice and num_ones % 4 == 2: 
            a += 2 

        return a
