func minFlips(grid [][]int) (ans int) {
    n, m := len(grid), len(grid[0])

    for i:=0; i<n/2; i++ {
        for j:=0; j<m/2; j++ {
            res := grid[i][j] + grid[i][m-j-1] + grid[n-i-1][j] + grid[n-i-1][m-j-1]
            ans += min(res, 4 - res)
        }
    }

    if n&1==1 && m&1==1 && grid[n/2][m/2]==1 {
        ans++
    }

    // fmt.Println(ans)

    arr := [3]int{0,0,0}

    if n & 1 == 1 {
        for j:=0; j<m/2; j++ {
            arr[grid[n/2][j] + grid[n/2][m-j-1]]++
        }
    }

    if m&1==1 {
        for i:=0; i<n/2; i++ {
            arr[grid[n-i-1][m/2]+grid[i][m/2]]++
        }
    }

    // fmt.Println(arr)

    if arr[1] == 0 {
        if arr[2] & 1 == 1 {
            ans += 2
        }
    } else {
        ans += arr[1]
    }

    return
}
