class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        ans = 0
        for x in range(len(grid)//2):
            for y in range(len(grid[x])//2):
                o=int(grid[x][y]==1)+int(grid[len(grid)-1-x][y]==1)\
                +int(grid[x][len(grid[x])-1-y]==1)\
                +int(grid[len(grid)-1-x][len(grid[x])-1-y]==1)
                z=int(grid[x][y]==0)+int(grid[len(grid)-1-x][y]==0)\
                +int(grid[x][len(grid[x])-1-y]==0)\
                +int(grid[len(grid)-1-x][len(grid[x])-1-y]==0)
                ans+=4-max(o,z)
        oods = 0
        revodds = 0
        if len(grid)%2:
            l=0
            r=len(grid[0])-1
            while l<r:
                if grid[len(grid)//2][l]!=grid[len(grid)//2][r]:
                    ans+=1
                    revodds+=1
                if grid[len(grid)//2][l]==1 and grid[len(grid)//2][r]==1:
                    oods+=1
                l+=1
                r-=1
        if len(grid[0])%2:
            l=0
            r=len(grid)-1
            while l<r:
                if grid[l][len(grid[0])//2]!=grid[r][len(grid[0])//2]:
                    ans+=1
                    revodds+=1
                if grid[l][len(grid[0])//2]==1 and grid[r][len(grid[0])//2]==1:
                    oods+=1
                l+=1
                r-=1
        if oods%2 and revodds==0:
            ans+=2
        if len(grid)%2 and len(grid[0])%2 and grid[len(grid)//2][len(grid[0])//2]:
            ans+=1
        return ans
