class Solution {
    public int minFlips(int[][] grid) {
        final int m = grid.length, n = grid[0].length;
        int count = 0;
        for (int i = 0; i < m / 2; i++) {
            for (int j = 0; j < n / 2; j++) {
                int c1 = 0;
                c1 += grid[i][j];
                c1 += grid[i][n - 1 - j];
                c1 += grid[m - 1 - i][j];
                c1 += grid[m - 1 - i][n - 1 - j];
                if (c1 == 2) {
                    count += 2;
                } else if (c1 == 1 || c1 == 3) {
                    count += 1;
                }
            }
        }
        if (m % 2 == 1 && n % 2 == 1) {
            int diffs = 0;
            for (int i = 0; i < n / 2; i++) {
                if (grid[m / 2][i] != grid[m / 2][n - 1 - i]) {
                    count++;
                    diffs++;
                }
            }
            for (int i = 0; i < m / 2; i++) {
                if (grid[i][n / 2] != grid[m - i - 1][n / 2]) {
                    diffs++;
                    count++;
                }
            }
            if (grid[m / 2][n / 2] == 1) {
                count++;
            }
            if (diffs == 0) {
                int ones = 0;
                for (int i = 0; i < m; i++) {
                    if (i != m / 2) {
                        ones += grid[i][n / 2];
                    }
                }
                for (int i = 0; i < n; i++) {
                    if (i != n / 2) {
                        ones += grid[m / 2][i];
                    }
                }
                if (ones % 4 == 2) {
                    count += 2;
                }
            }
        } else if (m % 2 == 1) {
            int diffs = 0;
            for (int i = 0; i < n / 2; i++) {
                if (grid[m / 2][i] != grid[m / 2][n - 1 - i]) {
                    count++;
                    diffs++;
                }
            }
            if (diffs == 0) {
                int ones = 0;
                for (int i = 0; i < n; i++) {
                    ones += grid[m / 2][i];
                }
                if (ones % 4 == 2) {
                    count += 2;
                }
            }
        } else if (n % 2 == 1) {
            int diffs = 0;
            for (int i = 0; i < m / 2; i++) {
                if (grid[i][n / 2] != grid[m - i - 1][n / 2]) {
                    diffs++;
                    count++;
                }
            }
            if (diffs == 0) {
                int ones = 0;
                for (int i = 0; i < m; i++) {
                    ones += grid[i][n / 2];
                }
                if (ones % 4 == 2) {
                    count += 2;
                }
            }
        }
        return count;
    }
}
