class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;++i){
            for(int j=0;j<m/2;++j){
                int c=0;
                if(grid[i][j])c++;
                if(grid[n-1-i][j])c++;
                if(grid[i][m-1-j])c++;
                if(grid[n-1-i][m-1-j])c++;
                ans+=min(c,4-c);
            }
        }
        int cnt=0;
        int c=0;
        if(n&1){
            for(int i=0;i<m/2;++i){
                if(grid[n/2][i] && grid[n/2][m-1-i])c+=2;
                cnt+=grid[n/2][i]!=grid[n/2][m-1-i];
            }
        }
        if(m&1){
            for(int i=0;i<n/2;++i){
                if(grid[i][m/2] && grid[n-1-i][m/2])c+=2;
                cnt+=grid[i][m/2]!=grid[n-1-i][m/2];
            }
        }
        if(c%4==0){
            if(n%2 && m%2 && grid[n/2][m/2])ans++;
            return ans+cnt;
        }
        else{
            if(n%2 && m%2 && grid[n/2][m/2])ans++;
            return ans+cnt+(cnt==0?2:0);
        }
    }
};
