class Solution {
public:
   int count(vector<int>& a){
        vector<int> b=a;
        reverse(b.begin(),b.end());
        int ret=0;
        for(int i=0;i<(b.size())/2;i++){
            if(a[i]!=b[i])ret++;
        }
        return ret;
    }
    int fun(vector<int>& a){
        int ret=0;
        for(int i=0;i<a.size();i++){
            if(a[i])ret++;
        }
        return ret;
    }
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ret=0;
        for(int i=0;i<m/2;i++)
            for(int j=0;j<n/2;j++){
                int ct1;
                ct1=0;
                ct1=grid[i][j]+   
                grid[i][n-1-j]+
                grid[m-1-i][j]+  
                grid[m-1-i][n-1-j];
                ret += min(ct1,4-ct1);
            }
            int tm=m;
            int tn=n;
            tm/=2;
            tn/=2;
        if((n&1)&&(m&1)){
           bool tag=true;
           int tp = count(grid[tm]);
           vector<int> a;
           for(int i=0;i<m;i++){
                a.push_back(grid[i][tn]);
           }     
           tp += count(a);
           ret += tp;
           if(tp){
             if(grid[tm][tn]) ret++;

           }
           else{
              if(grid[tm][tn]) ret++;
              int ct=0;
              ct += fun(a)+fun(grid[tm]);
              if(grid[tm][tn]) ct-=2;
              if(ct%4) ret+=2;

           }

        }   
        else if(n&1){
           vector<int> a;
           int tp;
           for(int i=0;i<m;i++){
                a.push_back(grid[i][tn]);
           }     
           ret += (tp=count(a));
           if(tp==0){
                int cc=fun(a);
                if(cc%4) ret+=2;
           }
        }   
        else if(m&1){
           vector<int> a;
           int tp;
           a = grid[tm];
           ret += (tp=count(a));
           if(tp==0){
               int cc=fun(a);
               if(cc%4) ret+=2;
           }



        }
        return ret;
    }
};
