class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        ans = 0
        for i in range(m//2):
            for j in range(n//2):
                c = grid[i][j] + grid[-i-1][j] + grid[i][-j-1] + grid[-i-1][-j-1]
                if c > 2:
                    ans += 4 - c
                else:
                    ans += c
        if m % 2 == 1 and n % 2 == 1:
            ans += grid[m//2][n//2]
        dis = 0
        ones = 0
        if m % 2 == 1:
            row = grid[m//2]
            for j in range(n // 2):
                dis += row[j] ^ row[-j-1]
                ones += row[j] + row[-j-1]
        if n % 2 == 1:
            for i in range(m // 2):
                dis += grid[i][n//2] ^ grid[-i-1][n//2]
                ones += grid[i][n//2] + grid[-i-1][n//2]

        if (ones + dis) % 4 == 0:
            return ans + dis
        else:
            m = 4
            for x in range(ones-dis, ones+dis+1, 2):
                m = min(m, x % 4)
            return ans + dis + m
