class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size() ;
        int m = grid[0].size() ;
        int ans = 0 ;

        for(int i = 0 ; i<(n/2) ; i++){
            for(int j = 0 ; j<(m/2) ; j++){
                int c0 = 0 ;
                int c1 = 0 ;
                if (grid[i][j] == 0) c0++ ; else c1++ ;
                if (grid[i][m-1-j] == 0) c0++ ; else c1++ ;
                if (grid[n-1-i][j] == 0) c0++ ; else c1++ ;
                if (grid[n-1-i][m-1-j] == 0) c0++ ; else c1++ ;

                ans += 4-max(c0,c1) ;
            }
        }
        // cout << ans << endl ;

        if ((n%2) || (m%2) ){
            if ((n%2) && (m%2) &&  (grid[n/2][m/2] == 1)) ans++ ;
            int tmp = 0 ;
            int oneCnt = 0 ;
            if (n%2){
                for(int j = 0 ; j<m/2 ; j++){
                    if (grid[n/2][j] != grid[n/2][m-1-j]) tmp++ ;
                    else if (grid[n/2][j] == 1) oneCnt++ ;
                }
            }
            if (m%2){
                for(int i = 0 ; i<n/2 ; i++){
                    if (grid[i][m/2] != grid[n-1-i][m/2]) tmp++ ;
                    else if (grid[i][m/2] == 1) oneCnt++ ;
                }
            }

            if ((oneCnt%2) && (tmp==0)) ans+=2 ;
            else ans +=tmp ;
        }

        return ans ;
    }
};
