class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(), n=grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;++i){
            for(int j=0;j<n/2;++j){
                int num_0=0;
                if(grid[i][j]==0){
                    num_0++;
                }
                if(grid[m-i-1][j]==0){
                    num_0++;
                }
                if(grid[i][n-j-1]==0){
                    num_0++;
                }
                if(grid[m-i-1][n-j-1]==0){
                    num_0++;
                }
                if(num_0<2){
                    ans += num_0;
                }else{
                    ans += 4-num_0;
                }
            }
        }
        int num_1_hang=0,ans_hang=0;
        if(m%2==1){
            int i=m/2;
            int left=0,right=n-1;
            while(left<right){
                if(grid[i][left] != grid[i][right]){
                    ans_hang++;
                }else{
                    if(grid[i][left] == 1){
                        num_1_hang += 2;
                    }
                }
                left++;
                right--;
            }
        }
        int num_1_lie=0,ans_lie=0;
        if(n%2==1){
            int j=n/2;
            int top=0,bottom=m-1;
            while(top<bottom){
                if(grid[top][j] != grid[bottom][j]){
                    ans_lie++;
                }else{
                    if(grid[top][j] == 1){
                        num_1_lie += 2;
                    }
                }
                top++;
                bottom--;
            }
        }
        if(m%2==1 && n%2==1){
            if(grid[m/2][n/2]==1){
                ans++;
            }
        }
        if(m%2==1 || n%2==1){
            int num_1 = num_1_lie + num_1_hang;
            int need_1 = num_1 % 4;
            if(need_1!=0){
                if(ans_hang==0 && ans_lie==0){
                    ans += 2;
                }
            }
            ans += ans_hang + ans_lie;
        }
        return ans;
    }
};
