class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        const int row = grid.size();
        const int col = grid[0].size();

        int count = 0;

        int mid_row = grid.size() / 2;
        int mid_col = grid[0].size() / 2;
        int count_1 = 0;
        if (grid.size() % 2 == 1 && grid[0].size() % 2 == 1) {
            for (int i = 0, j = grid[0].size() - 1; i < j; i++, j--) {
                if (grid[mid_row][i] != grid[mid_row][j]) {
                    count++;
                }
                count_1 += grid[mid_row][i] + grid[mid_row][j];
            }
            for (int i = 0, j = grid.size() - 1; i < j; i++, j--) {
                if (grid[i][mid_col] != grid[j][mid_col]) {
                    count++;
                }
                count_1 += grid[i][mid_col] + grid[j][mid_col];
            }            
        } else if (grid.size() % 2 == 1) {
            for (int i = 0, j = grid[0].size() - 1; i < j; i++, j--) {
                if (grid[mid_row][i] != grid[mid_row][j]) {
                    count++;
                }
                count_1 += grid[mid_row][i] + grid[mid_row][j];
            }
        } else if (grid[0].size() % 2 == 1) {
            for (int i = 0, j = grid.size() - 1; i < j; i++, j--) {
                if (grid[i][mid_col] != grid[j][mid_col]) {
                    count++;
                }
                count_1 += grid[i][mid_col] + grid[j][mid_col];
            }
        }
        count = max(count, min(count_1 % 4, 4 - (count_1 % 4)));

        if (grid.size() % 2 == 1 && grid[0].size() % 2 == 1) {
            if (grid[mid_row][mid_col] == 1) {
                count++;
            }
        }

        for (int r_i = 0, r_j = row - 1; r_i < r_j; r_i++, r_j--) {
            for (int c_i = 0, c_j = col - 1; c_i < c_j; c_i++, c_j--) {
                int count_1 = grid[r_i][c_i] + grid[r_i][c_j] + grid[r_j][c_i] + grid[r_j][c_j];
                count += min(count_1, 4 - count_1);
            }
        }

        return count;

    }
};
