class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();
        int ans = 0;

        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int cnt = a[i][j] + a[n - 1 - i][j] + a[i][m - 1 - j] + a[n - 1 - i][m - 1 - j];
                ans += min(cnt, 4 - cnt);
            }
        }

        int c = 0;
        bool ok = false;
        if(n & 1){
            for(int j=0; j<m/2; j++){
                ans += a[n / 2][j] != a[n / 2][m - 1 - j];
                ok |= (a[n / 2][j] != a[n / 2][m - 1 - j]);
                if(a[n / 2][j] == 1 && a[n / 2][m - 1 - j] == 1)   c++; 
            }
        }

        if(m & 1){
            for(int i=0; i<n/2; i++){
                ans += a[i][m / 2] != a[n - 1 - i][m / 2];
                ok |= (a[i][m / 2] != a[n - 1 - i][m / 2]);
                if(a[i][m / 2] && a[n - 1 - i][m / 2])  c++;
            }
        }

        if(c & 1){
            if(!ok) ans += 2;
        }

        if((n&1) && (m&1) && (a[n / 2][m / 2])) ans++;

        return ans;
    }
};
