class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])

        ones = 0
        total = m*n
        flips = 0
        for i in range(m//2):
            for j in range(n//2):
                total -= 4 
                temp_1 = 0
                temp_0 = 0

                if(grid[i][j] == 1):
                    temp_1 += 1 
                else:
                    temp_0 += 1

                if(grid[i][n - j - 1] == 1):
                    temp_1 += 1 
                else:
                    temp_0 += 1

                if(grid[m - i - 1][n - j - 1] == 1):
                    temp_1 += 1 
                else:
                    temp_0 += 1

                if(grid[m - i - 1][j] == 1):
                    temp_1 += 1 
                else:
                    temp_0 += 1

                if(temp_1 == temp_0):
                    flips += 2
                elif (temp_1 == 1 or temp_0 == 1):
                    flips += 1

        next_flips = 0
        ones = 0
        if (m%2):
            for i in range(n//2):
                if grid[m//2][i] != grid[m//2][n - i - 1]:
                    next_flips += 1
                elif grid[m//2][i] == 1:
                    ones += 2

        if(n%2):
            for i in range(m//2):
                if grid[i][n//2] != grid[m - i - 1][n//2]:
                    next_flips += 1
                elif grid[i][n//2] == 1:
                    ones += 2

        if(n%2 == 1 == m%2):
            if(grid[m//2][n//2] == 1):
                flips += 1

        if(ones%4 == 2):
            if(next_flips == 0):
                flips += 2

        return flips + next_flips
