class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        int ans = 0;
        for (int i = 0; i < m / 2; i++) {
            for (int j = 0; j < n / 2; j++) {
                int cnt0 = 0, cnt1 = 0;
                if (grid[i][j] == 1) cnt1++;
                else cnt0++;
                if (grid[i][n - 1 - j] == 1) cnt1++;
                else cnt0++;
                if (grid[m - 1 - i][j] == 1) cnt1++;
                else cnt0++;
                if (grid[m - 1 - i][n - 1 - j] == 1) cnt1++;
                else cnt0++;
                ans += Math.min(cnt0, cnt1);
                // System.out.println("ans = " + ans);
            }
        }
        int change = 0;
        int cnt = 0; // 一对 1 的个数
        if (m % 2 == 1) {
            for (int le = 0, ri = n - 1; le < ri; le++, ri--) {
                if (grid[m / 2][le] != grid[m / 2][ri]) {
                    ans++;
                    change++;
                } else {
                    if (grid[m / 2][le] == 1) {
                        cnt++;
                    }
                }
            }
        }
        if (n % 2 == 1) {
            for (int le = 0, ri = m - 1; le < ri; le++, ri--) {
                if (grid[le][n / 2] != grid[ri][n / 2]) {
                    ans++;
                    change++;
                } else {
                    if (grid[le][n / 2] == 1) {
                        cnt++;
                    }
                }
            }
            if (m % 2 == 1 && grid[m / 2][n / 2] == 1) {
                ans++;
            }
        }
        if (cnt % 2 == 1 && change == 0) {
            ans += 2;
        }
        return ans;
    }
}
