class Solution {
public:
int minFlips(vector<vector<int>>& g) {
    int m = g.size(), n = g[0].size();
    int res = 0, ones = 0, flips = 0, center = 0;
    for (int i = 0; i < (m + 1) / 2; ++i)
        for (int j = 0; j < (n + 1) / 2; ++j) {
            int i1 = m - 1 - i, j1 = n - 1 - j;
            if (i == i1 && j == j1) {
                center = g[i][j];
            }
            else if (i == i1) {
                flips += g[i][j] != g[i][j1];
                res += g[i][j] != g[i][j1];
                if (g[i][j] + g[i][j1] == 2)
                    ones += 2;
            }
            else if (j == j1) {
                flips += g[i][j] != g[i1][j];
                res +=  g[i][j] != g[i1][j];
                if (g[i][j] + g[i1][j] == 2)
                    ones += 2;                
            }
            else  {
                int ones = g[i][j] + g[i1][j] + g[i][j1] + g[i1][j1];
                res += min(ones, 4 - ones);                
            }
        }
    // cout << ones << " " << flips << endl;
    return res + center + (ones % 4 && flips == 0 ? 2 : 0);
}
};
