class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n = grid.size(), m = grid[0].size();
        int sem = 0;
        // cout << n << ' ' << m << endl;
        for(int i=0; i<n/2; i++) {
            for(int j=0; j<m/2; j++) {
                int cnt =0;
                if(grid[i][j])  cnt++;
                if (grid[n-i-1][j]) cnt++;
                if(grid[i][m-j-1]) cnt++;
                if(grid[n-i-1][m-j-1]) cnt++;
                cout << cnt << endl;

                ans += min(cnt, 4-cnt);
            }
        }
        int ch =0;
        if (n&1) {
            int mid = n/2;
            for(int j=0; j<m/2; j++) {
                if (grid[mid][j] && grid[mid][m-j-1]) sem+=2;
                else if (grid[mid][j] || grid[mid][m-j-1]) {
                    sem+=2;
                    ch++;
                }
            }
        }
        if (m&1) {
            for(int i=0; i<n/2; i++) {
                if (grid[i][m/2] && grid[n-i-1][m/2]) sem+=2;
                else if (grid[i][m/2] || grid[n-i-1][m/2]) {
                    sem+=2;
                    ch++;
                }
            }   
        }
        if((n&1)&& (m&1)) {
            if (grid[n/2][m/2]) ans++;
        }
        ans += ch;
        cout << sem << ' ' << ch << endl;
        if (sem%4==2) {
            ans += max(0, 2-ch*2);
        }

        return ans;
    }
};
