class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        int ans = 0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int cnt = grid[i][j] + grid[i][n-1-j] + grid[m-1-i][j] + grid[m-1-i][n-1-j];
                ans += Math.min(cnt, 4 - cnt);
            }
        }
        if((m & 1) == 1 && (n & 1) == 0){
            int mid = m / 2;
            int temp = 0;
            int pair = 0;
            for(int j=0;j<n/2;j++){
                if(grid[mid][j] == grid[mid][n-1-j]){
                    if(grid[mid][j] == 1){
                        pair++;
                    }
                }else{
                    temp++;
                }
            }
            if(pair % 2 == 0){
                ans += temp;
            }else{
                if(temp >= 1){
                    ans += temp;
                }else {
                    ans += 2;
                    ans += temp;
                }
            }
        }
        else if((n & 1) == 1 && (m & 1) == 0){
            int mid = n / 2;
            int temp = 0;
            int pair = 0;
            for(int i=0;i<m/2;i++){
                if(grid[i][mid] == grid[m-1-i][mid]){
                    if(grid[i][mid] == 1){
                        pair++;
                    }
                }else{
                    temp++;
                }
            }
            if(pair % 2 == 0){
                ans += temp;
            }else{
                if(temp >= 1){
                    ans += temp;
                }else {
                    ans += 2;
                    ans += temp;
                }
            }
        }
        else if((m & 1) == 1 && (n & 1) == 1){
            int rmid = m / 2;
            int cmid = n / 2;
            int temp = 0;
            int pair = 0;
            for(int j=0;j<n/2;j++){
                if(grid[rmid][j] == grid[rmid][n-1-j]){
                    if(grid[rmid][j] == 1){
                        pair++;
                    }
                }else{
                    temp++;
                }
            }
            for(int i=0;i<m/2;i++){
                if(grid[i][cmid] == grid[m-1-i][cmid]){
                    if(grid[i][cmid] == 1){
                        pair++;
                    }
                }else{
                    temp++;
                }
            }
            if(pair % 2 == 0){
                ans += temp;
            }else{
                if(temp >= 1){
                    ans += temp;
                }else {
                    ans += 2;
                    ans += temp;
                }
            }
            ans += grid[m / 2][n / 2];
        }
        return ans;
    }
}
