class Solution {
public:
    int getans(int ind, int sum, vector<pair<int,int>>&vp, vector<vector<int>>&dp){
        if(ind >= vp.size()){
            if(sum == 0){
                return 0;
            }
            return 1e9;
        }

        if(dp[ind][sum] != -1){
            return dp[ind][sum];
        }

        int ans = 1e9;
        int ts = sum;
        ts = (ts + vp[ind].first + vp[ind].second)%4;
        ans = min(ans, vp[ind].first + getans(ind+1,ts,vp,dp));
        ts = sum;
        ans = min(ans, vp[ind].second + getans(ind+1,ts,vp,dp));
        return dp[ind][sum] = ans;
    }


    int minFlips(vector<vector<int>>& grid) {
        vector<pair<int,int>>vp;
        int n = grid.size();
        int m = grid[0].size();

        int i = 0, j = n-1;
        while(i < j){
            int a = 0, b = m-1;
            while(a < b){
                vector<int>cnt(2,0);
                cnt[grid[i][a]]++;
                cnt[grid[i][b]]++;
                cnt[grid[j][a]]++;
                cnt[grid[j][b]]++;
                vp.push_back({cnt[0],cnt[1]});
                a++;
                b--;
            }

            if(a == b){
                vector<int>cnt(2,0);
                cnt[grid[i][a]]++;
                cnt[grid[j][b]]++;
                vp.push_back({cnt[0],cnt[1]});
            }
            i++;
            j--;
        }   

        if(i == j){
            int a = 0, b = m-1;
            while(a < b){
                vector<int>cnt(2,0);
                cnt[grid[i][a]]++;
                cnt[grid[i][b]]++;
                vp.push_back({cnt[0],cnt[1]});
                a++;
                b--;
            }
            if(a == b){
                vector<int>cnt(2,0);
                cnt[grid[i][a]]++;
                vp.push_back({cnt[0],cnt[1]});
            }
        }

        int sz = vp.size();
        // for(auto it : vp){
        //     cout << it.first << " " << it.second << "~";
        // }
        // cout << endl;
        vector<vector<int>>dp(sz+1,vector<int>(4,-1));
        int ans = getans(0,0,vp,dp);
        return ans;
    }
};
