class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int sum=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int c1=0,c0=0;
                if(grid[i][j]==0)c0++;
                else c1++;
                if(grid[i][m-1-j]==0)c0++;
                else c1++;
                 if(grid[n-1-i][j]==0)c0++;
                else c1++;
                 if(grid[n-1-i][m-1-j]==0)c0++;
                else c1++;
                sum+=min(4-c0,4-c1);
            }
        }
        if(n%2==0&&m%2==0)
        return sum;
        if(m%2==1&&n%2==1){
            if(grid[n/2][m/2]==1){
                sum++;grid[n/2][m/2]=0;
            }
        }
        int count1=0,count2=0;
        if(n%2==1){
            for(int i=0;i<m/2;i++){
              if(grid[n/2][i]!=grid[n/2][m-1-i])count1++;
              if(grid[n/2][i]==grid[n/2][m-1-i]&&grid[n/2][m-1-i])count2++;

            }
        }
        if(m%2==1){
             for(int i=0;i<n/2;i++){
              if(grid[i][m/2]!=grid[n-1-i][m/2])count1++;
              if(grid[i][m/2]==grid[n-1-i][m/2]&&grid[n-1-i][m/2])count2++;

            }
        }
        if(count2%2==0){
              return sum+count1;
        }
        if(count1==0){
            return (sum+2);
        }
        return sum+count1;
    }
};
