class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0, ops = 0, ones = 0;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                int ct[2] = {0, 0};
                ct[grid[i][j]]++;
                ct[grid[i][m - 1 - j]]++;
                ct[grid[n - 1 - i][j]]++;
                ct[grid[n - 1 - i][m - 1 - j]]++;
                if ((n % 2 and i == n / 2) or (m % 2 and j == m / 2)) {
                    if (i == n / 2 and j == m / 2) {
                        ans += grid[i][j];
                        grid[i][j] = 0;
                    } else {
                        if (max(ct[0], ct[1]) == 4) {
                            if (ct[1] == 4) ones += 2;
                        } else {
                            ++ops;
                        }
                    }
                } else {
                    ans += 4 - max(ct[0], ct[1]);
                }

                grid[i][j] = grid[i][m - 1 - j] = grid[n - 1 - i][j] = grid[n - 1 - i][m - 1 - j] = 0;
            }
        }
        if (ones % 4 == 0) {
            ans += ops;
            return ans;
        }
        if (ops) ans += ops;
        else ans += ones % 4;
        return ans;
    }
};
