class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size();
        int m = g[0].size();
        int sol = 0;
        if((n & 1) && (m & 1) ){
            if(g[(n-1)/2][(m-1)/2]){
                sol++;
                g[(n-1)/2][(m-1)/2]= 0;
            }
        }
        int c = 0; bool f = 0;
        if(n & 1){
           rep(i , 0 , m/2){
            if(g[(n-1)/2][i] != g[(n-1)/2][m-1-i]){
                c+= 2;
                f = 1;
                sol++;
            }
            else if(g[(n-1)/2][i] == 1){
                c+= 2;
            }
           }
        }

        if(m & 1){
              rep(i ,  0 , n/2){
                if(g[i][(m-1)/2] != g[n-1-i][(m-1)/2]){
                    c += 2;
                    f = 1;
                    sol++;
                }
                else if(g[i][(m-1)/2]){
                    c += 2;
                }
              }
        }
        if(c % 4 != 0 && !f){
            sol += 2;
        }
        cout << sol <<" ";
        rep(i , 0 , n/2 ){
            rep(j , 0 , m/2){
                int nc = 0;
                if(g[i][j] == 1){
                    nc ++;
                }
                if(g[n-1-i][j]){nc ++;}
                if(g[i][m-1-j]){nc++;}
                if(g[n-1-i][m-1-j]){nc++;}
                sol += min(4-nc, nc);
            }
        }
        return sol;

    }
