class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int l1=0,l2=n-1,r1=0,r2=m-1;
        bool flag=false;
        int ans=0;
        while(l1<l2){
            r1=0,r2=m-1;
            while(r1<r2){
                int one=0,zero=0;
                if(grid[l1][r1]==0) zero++;
                else one++;
                if(grid[l1][r2]==0) zero++;
                else one++;
                if(grid[l2][r1]==0) zero++;
                else one++;
                if(grid[l2][r2]==0) zero++;
                else one++;

                    if(one>=zero) {
                        flag=true;
                        ans+=(4-one);
                    }
                    else{
                        ans+=(4-zero);
                }
                r1++;
                r2--;
            }
            l1++;
            l2--;
        }
        int v1=0,v2=0,v3=0,v4=0;
        if(n%2!=0){
            int l=0,r=m-1;

            while(l<r){
                if(grid[n/2][l]!=grid[n/2][r]){
                    ans++;
                    v2++;
                }
                else if(grid[n/2][l]==1) v1++;
                l++;
                r--;
            }
        }
        if(m%2!=0){
            int l=0,r=n-1;
            while(l<r){
                if(grid[l][m/2]!=grid[r][m/2]){
                    ans++;
                    v3++;
                }
                else if(grid[l][m/2]==1){
                    v4++;
                }
                l++;
                r--;
            }
        }
        if((v1+v4)%2!=0){
            if(v2==0&&v3==0){
                ans+=2;
            }
        }
        if(n%2!=0&&m%2!=0&&(grid[n/2][m/2]==1)) ans++;
        return ans;
    }
};
