class Solution(object):
    def minFlips(self, grid):
        nr = len(grid)
        nc = len(grid[0])
        flip = 0
        for r in range(nr // 2):
            for c in range(nc // 2):
                mr = nr - r - 1
                mc = nc - c - 1
                count = grid[r][c] 
                count += grid[mr][c] 
                count += grid[r][mc] 
                count += grid[mr][mc] 
                if count == 1:
                    flip += 1
                if count == 2:
                    flip += 2
                elif count == 3:
                    flip += 1
        flipped = False
        one_count = 0
        if nc % 2 == 1:
            for r in range(nr // 2):
                mr = nr - r - 1
                if grid[r][nc // 2] != grid[mr][nc // 2]:
                    grid[r][nc // 2] = grid[mr][nc // 2]
                    flipped = True
                    flip += 1
            for r in range(nr):
                one_count += grid[r][nc // 2]
        if nr % 2 == 1:
            for c in range(nc // 2):
                mc = nc - c - 1
                if grid[nr // 2][c] != grid[nr // 2][mc]:
                    grid[nr // 2][c] = grid[nr // 2][mc]
                    flipped = True
                    flip += 1
            for c in range(nc):
                one_count += grid[nr // 2][c]
        if nr % 2 == 1 and nc % 2 == 1:
            one_count -= grid[nr // 2][nc // 2]
        if one_count % 4 == 1:
            flip += 1
        elif one_count % 4 == 2:
            if not flipped:
                flip += 2
        elif one_count % 4 == 3:
            if flipped:
                flip += 1
            else:
                flip += 3
        return flip
