class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])

        result = 0
        for i in range(m // 2):
            for j in range(n // 2):
                a = grid[i][j]
                b = grid[i][-1 - j]
                c = grid[-1 - i][j]
                d = grid[-1 - i][-1 - j]
                sum_ = a + b + c + d
                current = min(sum_, 4 - sum_)
                result += current

        first = 0
        first_ones = 0
        if m % 2 == 1:
            i = m // 2
            for j in range(n // 2):
                a = grid[i][j]
                b = grid[i][-1 - j]
                if a != b:
                    first += 1
                    first_ones += 2
                else:
                    first_ones += a + b

        second = 0
        second_ones = 0
        if n % 2 == 1:
            j = n // 2
            for i in range(m // 2):
                a = grid[i][j]
                b = grid[-1 - i][j]
                if a != b:
                    second += 1
                    second_ones += 2
                else:
                    second_ones += a + b

        is_center = (m % 2 == 1) and (n % 2 == 1)

        rem = (first_ones + second_ones) % 4

        if rem == 0:
            if is_center:
                if grid[m // 2][n // 2] == 1:
                    return result + first + second + 1
            return result + first + second

        if first + second >= 1:
            if is_center:
                if grid[m // 2][n // 2] == 1:
                    return result + first + second + 1
            return result + first + second

        answer = result + 2
        if is_center:
            if grid[m // 2][n // 2] == 1:
                answer += 1
        return answer
