class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        cnt = 0
        for i in range(m // 2):
            for j in range(n // 2):
                tmp = grid[i][j] + grid[m - 1 - i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][n - 1 - j]
                if tmp == 2:
                    cnt += 2
                elif tmp == 1 or tmp == 3:
                    cnt += 1

        if m % 2 == 0 and n % 2 == 0:
            return cnt
        elif m % 2 and n % 2:
            cnt += grid[m // 2][n // 2] != 0

        cnt1, cnt2 = 0, 0
        if m % 2:
            for j in range(n // 2):
                cnt1 += grid[m // 2][j] != grid[m // 2][n - 1 - j]
                cnt2 += grid[m // 2][j] == 1 and grid[m // 2][n - 1 - j] == 1
        if n % 2:
            for i in range(m // 2):
                cnt1 += grid[i][n // 2] != grid[m - 1 - i][n // 2]
                cnt2 += grid[i][n // 2] == 1 and grid[m - 1 - i][n // 2] == 1

        if cnt2 % 2 == 0:
            return cnt + cnt1
        elif cnt1 == 0:
            return cnt + cnt1 + 2
        else:
            return cnt + cnt1
