class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        ans = sum(sum(g) for g in grid)
        m, n = len(grid), len(grid[0])

        tmp = 0
        for i in range(m//2):
            for j in range(n//2):
                cnt = grid[i][j]+grid[-i-1][j]+grid[i][-j-1]+grid[-i-1][-j-1]
                tmp += min(cnt, 4-cnt)

        cnt = 0
        diff = 0
        if m&1:
            for j in range(n//2):
                diff += grid[m//2][j]!=grid[m//2][-j-1]
                cnt += grid[m//2][j]|grid[m//2][-j-1]

        if n&1:
            for i in range(m//2):
                diff += grid[i][n//2]!=grid[-i-1][n//2]
                cnt += grid[i][n//2]|grid[-i-1][n//2]
        tmp += diff
        if cnt&1 and diff==0:
            tmp += 2
        if m&1 and n&1:
            tmp += grid[m//2][n//2]

        return min(ans, tmp)
