class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        vector<vector<int>>vc;
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        int cnt00 = 0, cnt11 = 0, cnt01 = 0;
        for(int i = 0;i <= n - i - 1;i++)
        {
            for(int j=0;j <= m - j - 1;j++)
            {
                if(i != n - i - 1 and j != m - j -1)
                {
                    int s = grid[i][j] + grid[i][m - 1 - j] + grid[n - i - 1][j] + grid[n - i - 1][m - j - 1];
                    ans += min(s, 4 - s);
                }
                else
                {
                    if(i == n - i - 1 and j == m - j -1)
                    {
                        ans += grid[i][j];
                    }
                    else
                    {
                        if(i == n - i - 1)
                        {
                            if(grid[i][j] == grid[i][m - j - 1])
                            {
                                if(grid[i][j])
                                cnt11++;
                                else
                                cnt00++;
                            }
                            else
                            cnt01++;
                        }
                        else
                        {
                            if(grid[i][j] == grid[n - i - 1][j])
                            {
                                if(!grid[i][j])
                                cnt00++;
                                else
                                cnt11++;
                            }
                            else
                            cnt01++;
                        }
                    }
                }
            }
        }

        if(cnt11 % 2 == 0)
        {
            ans += cnt01;
        }
        else
        {
            if(cnt01)
            {
                ans += cnt01;
            }
            else
            ans += 2;
        }
        return ans;

    }
};
