class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0
        for i in range(m // 2):
            for j in range(n // 2):
                res = grid[i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][j] + grid[m - 1 - i][n - 1 - j]
                ans += min(res, 4 - res)
        if m & 1 == 0 and n & 1 == 0:
            return ans
        elif m & 1 == 0:
            col = n // 2
            cnt11 = cnt01 = 0
            for i in range(m // 2):
                if grid[i][col] == grid[m - 1 - i][col] == 1:
                    cnt11 += 1
                if grid[i][col] + grid[m - 1 - i][col] == 1:
                    cnt01 += 1
            if cnt01 == 0:
                ans += 2 * (cnt11 % 2)
            else:
                ans += cnt01
            return ans
        elif n & 1 == 0:
            row = m // 2
            cnt11 = cnt01 = 0
            for j in range(n // 2):
                if grid[row][j] == grid[row][n - 1 - j] == 1:
                    cnt11 += 1
                if grid[row][j] + grid[row][n - 1 - j] == 1:
                    cnt01 += 1
            if cnt01 == 0:
                ans += 2 * (cnt11 % 2)
            else:
                ans += cnt01
            return ans
        else:
            ans += grid[m // 2][n // 2]
            col = n // 2
            cnt11 = cnt01 = 0
            for i in range(m // 2):
                if grid[i][col] == grid[m - 1 - i][col] == 1:
                    cnt11 += 1
                if grid[i][col] + grid[m - 1 - i][col] == 1:
                    cnt01 += 1
            row = m // 2
            for j in range(n // 2):
                if grid[row][j] == grid[row][n - 1 - j] == 1:
                    cnt11 += 1
                if grid[row][j] + grid[row][n - 1 - j] == 1:
                    cnt01 += 1
            if cnt01 == 0:
                ans += 2 * (cnt11 % 2)
            else:
                ans += cnt01
            return ans
