class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        for(int i=0;i<(n)/2;i++){
            for(int j=0;j<m/2;j++){
                int val=grid[i][j];
                int c=0;
                if(grid[n-i-1][j]!=val)c++;
                if(grid[n-i-1][m-j-1]!=val)c++;
                if(grid[i][m-j-1]!=val)c++;
                if(c>=2)ans+=4-c;
                else ans+=c;
            }
        }
        if(n%2==0 && m%2==0){
            return ans;
        }
        if(n&1 && m&1 && grid[n/2][m/2]==1)ans++;
        int c=0,o=0;
        if(n&1){
            for(int i=0;i<m/2;i++){
                int val=grid[n/2][i];
                if(val!=grid[n/2][m-i-1])o++;
                else{
                    if(val==grid[n/2][m-i-1] && val==1)c+=2;
                }
            }
        }
        if(m&1){
            for(int i=0;i<n/2;i++){
                int val=grid[i][m/2];
                if(val!=grid[n-i-1][m/2])o++;
                else{
                    if(val==grid[n-i-1][m/2] && val==1)c+=2;
                }
            }
        }
        if(c%4==0)return ans+o;
        else{
            if(o>0)return ans+o;
            else return ans+2;
        }

    }
};
