class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int a=grid[i][j],b=grid[m-i-1][j],c=grid[i][n-j-1],d=grid[m-i-1][n-j-1];
                int one=a+b+c+d;
                if(one==2)
                    ans+=2;
                else if(one==1||one==3)
                    ans+=1;
            }
        }
        if(m%2==1&&n%2==1)
            ans+=grid[m/2][n/2];
        int changes=0,ones=0;
        if(m%2==1){
            for(int i=0;i<n/2;i++){

                if(grid[m/2][i]!=grid[m/2][n-i-1]){
                    ans++;
                    changes++;
                }
                else if(grid[m/2][i])
                    ones+=2;
            }
        }
        if(n%2==1){
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-i-1][n/2]){
                    ans++;
                    changes++;
                }
                else if(grid[i][n/2])
                    ones+=2;
            }
        }
        if(ones%4!=0&&changes==0)
            ans+=2;
        return ans;
    }
};
