class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int ones = grid[i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][j] + grid[n-1-i][m-1-j];
                ans += min(ones,4 - ones);
            }
        }

        if(m%2 != 0 && n%2 != 0)
        {
            ans += grid[n/2][m/2];
            int on = 0;
            bool isZeroPres = false;
            for(int i=0;i<n/2;i++)
            {
                on += grid[i][m/2] + grid[n-i-1][m/2];
                if(grid[i][m/2] != grid[n - i - 1][m/2])
                {
                    isZeroPres = true;
                    ans++;
                }
            }
            for(int j=0;j<m/2;j++)
            {
                on += grid[n/2][j] + grid[n/2][m-j-1];
                if(grid[n/2][j] != grid[n/2][m-j-1])
                {
                    isZeroPres = true;
                    ans++;
                }
            }
            if(!isZeroPres)
            {
                if(on%4 != 0)
                ans += 2;
            }

        }
        else if(m%2 != 0)
        {
            int on = 0;
            bool isZeroPres = false;
            for(int i=0;i<n/2;i++)
            {
                on += grid[i][m/2] + grid[n-i-1][m/2];
                if(grid[i][m/2] != grid[n - i - 1][m/2])
                {
                    isZeroPres = true;
                    ans++;
                }
            }
            if(!isZeroPres)
            {
                if(on%4 != 0)
                ans += 2;
            }

        }
        else if(n%2 != 0)
        {
            bool isZeroPres = false;
            int on = 0;
            for(int j=0;j<m/2;j++)
            {
                on += grid[n/2][j] + grid[n/2][m-j-1];
                if(grid[n/2][j] != grid[n/2][m-j-1])
                {
                    isZeroPres = true;
                    ans++;
                }
            }

            if(!isZeroPres)
            {
                if(on%4 != 0)
                ans += 2;
            }
        }

        return ans;
    }
};
