class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        vector<vector<int>>& g = grid;
        int m = g.size(), n = g[0].size();
        int a = 0;
        for(int i = 0; i < m/2; i++){
            for(int j = 0; j < n/2; j++){
                int c = g[i][j]+g[m-i-1][j]+g[i][n-j-1]+g[m-i-1][n-j-1];
                a += min(4-c,c);
            }
        }
        if((m%2)&&(n%2)&&g[m/2][n/2])
            a++;
        int b = 0,d = 0;
        if(n&1)
            for(int i = 0; i < m/2; i++){
                int c = g[i][n/2]+g[m-i-1][n/2];
                b += c%2;
                d += c/2;
            }
        if(m&1)  
            for(int j = 0; j < n/2; j++){
                int c = g[m/2][j]+g[m/2][n-j-1];
                b += c%2;
                d += c/2;
            }
        if(d%2 && b == 0)
            b+=2;
        return a+b;
    }
};
