class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int ans = 0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int zeros = 0, ones = 0;
                zeros += 1-grid[i][j];
                ones += grid[i][j];
                zeros += 1-grid[m-1-i][j];
                ones += grid[m-1-i][j];
                zeros += 1-grid[i][n-1-j];
                ones += grid[i][n-1-j];
                zeros += 1-grid[m-1-i][n-1-j];
                ones += grid[m-1-i][n-1-j];
                ans += 4-Math.max(zeros,ones);
            }
        }
        if(m%2 == 1 && n%2==1 && grid[m/2][n/2]==1){
            ans++;
        }
        int a = 0, b = 0;
        if(m%2 == 1){
            int mid = m/2;
            for(int i=0;i<n/2;i++){
                int z = 0, o = 0;
                z += 1-grid[mid][i];
                o += grid[mid][i];
                z += 1-grid[mid][n-1-i];
                o += grid[mid][n-1-i];
                ans += 2-Math.max(o,z);
                if(z==1){
                    a++;
                }else if(z==0){
                    b++;
                }
            }
        }
        if(n%2 == 1){
            int mid = n/2;
            for(int i=0;i<m/2;i++){
                int z = 0, o = 0;
                z += 1-grid[i][mid];
                o += grid[i][mid];
                z += 1-grid[m-1-i][mid];
                o += grid[m-1-i][mid];
                ans += 2-Math.max(o,z);
                if(z==1){
                    a++;
                }else if(z==0){
                    b++;
                }
            }
        }
        if(a == 0 && b%2==1){
            ans+=2;
        }
        return ans;
    }
}
