class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int res = 0;
        for(int i = 0, j = m - 1; i < j; ++i, --j) {
            for(int x = 0, y = n - 1; x < y; ++x, --y) {
                int cnt[2] = {0};
                cnt[grid[i][x]]++;
                cnt[grid[i][y]]++;
                cnt[grid[j][x]]++;
                cnt[grid[j][y]]++;
                res += min(cnt[0], cnt[1]);
            }
        }
        int temp = 0, temp1 = 0, temp2 = 0;
        if(m & 1) {
            int x = m / 2;
            for(int i = 0, j = n - 1; i < j; ++i, --j) {
                temp += grid[x][i] != grid[x][j];
                temp1 += grid[x][i] == 1 && grid[x][j] == 1;
            }
        }

        if(n & 1) {
            int x = n / 2;
            for(int i = 0, j = m - 1; i < j; ++i, --j) {
                temp += grid[i][x] != grid[j][x];
                temp1 += grid[i][x] == 1 && grid[j][x] == 1;
            }
        }

        if(temp1 & 1) {
            if(temp == 0) {
                res += 2;
            }
        }
        if(m % 2 == 1 && n % 2 == 1) {
            res += grid[m/2][n / 2];
        }
        return res + temp;
    }
};
