class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        int cnt=0;
        for(int i=0;i<m/2;++i){
            for(int j=0;j<n/2;++j){
                cnt=grid[i][j]+grid[m-i-1][j]+grid[i][n-j-1]+grid[m-i-1][n-j-1];
                ans+=min(cnt,4-cnt);
            }
        }
        if(n%2==1&&m%2==1&&grid[m/2][n/2]==1)
            ++ans;
        int cnt_1=0,cnt_2=0;
        if(m%2==1){
            for(int j=0;j<n/2;++j)
                if(grid[m/2][j]==1||grid[m/2][n-j-1]==1){
                    if(grid[m/2][j]==1&&grid[m/2][n-j-1]==1)
                        ++cnt_2;
                    else
                        ++cnt_1;
                }
        }
        if(n%2==1){
            for(int i=0;i<m/2;++i)
                if(grid[i][n/2]==1||grid[m-i-1][n/2]==1){
                    if(grid[i][n/2]==1&&grid[m-i-1][n/2]==1)
                        ++cnt_2;
                    else
                        ++cnt_1;
                }
        }
        if(cnt_2%2==1&&cnt_1==0)
            ans+=2;
        return ans+cnt_1;
    }
};
