class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int r = grid.size();
        int c = grid[0].size();

        int ones = 0, zero = 0;
        int ans = 0;

        if(c > 1 && r > 1) {
            for(int i=0; i<r/2; i++) {
                for(int j=0; j<c/2; j++) {
                    int ul = grid[i][j];
                    int ur = grid[i][c-j-1];
                    int bl = grid[r-i-1][j];
                    int br = grid[r-i-1][c-j-1];
                    vector<int> v = {ul, ur, bl, br};
                    int num1 = 0, num0 = 0;
                    for(int k : v) {
                        if(k)   num1++;
                        else    num0++;
                    }
                    if(num1 > num0) {
                        ones += 4;
                        ans += num0;
                    }
                    else {
                        zero += 4;
                        ans += num1;
                    }
                }
            }
        }


        if(r%2 && c%2) {
            int rowNum = r/2;
            int colNum = c/2;
            if(grid[r/2][c/2]) {
                ans++;
                grid[r/2][c/2] = 0;
            }
        }

        int zeroOne = 0;
        if(r%2) {
            for(int i=0; i<c/2; i++) {
                if(grid[r/2][i] && grid[r/2][c-i-1]) {
                    ones += 2;
                }
                else if(grid[r/2][i] != grid[r/2][c-i-1]) {
                    zeroOne++;
                    ans++;
                }
            }
        }

        if(c%2) {
            for(int i=0; i<r/2; i++) {
                if(grid[i][c/2] && grid[r-i-1][c/2]) {
                    ones += 2;
                }
                else if(grid[i][c/2] != grid[r-i-1][c/2]) {
                    zeroOne++;
                    ans++;
                }
            }
        }

        int rem = ones%4;
        if(!rem)    return ans;

        if(zeroOne) {
            return ans;
        }
        return ans+2;
    }
};
