class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size() , m = grid[0].size();
        int ansFin = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int p = grid[i][j], q = grid[n-1-i][j] ;
                int r = grid[i][m-1-j], s = grid[n-1-i][m-1-j];
                int ones = 0 ;
                if(p == 1) ones++;
                if(q == 1) ones++;
                if(r == 1) ones++;
                if(s == 1) ones++;
                ansFin += min(ones , (4 - ones));
            }
        }
        map<int,int>mp;
        if(n % 2 == 1){
            int row = n/2;
            int l = 0 , r = m-1;
            while(l < r){
                mp[(grid[row][l] + grid[row][r])]++;
                l++;r--;
            }
        }
        if(m % 2 == 1){
            int col = m/2;
            int l = 0 , r = n-1;
            while(l < r){
                mp[(grid[l][col] + grid[r][col])]++;
                l++;r--;
            }
        }
        if(n % 2 == 1 && m % 2 == 1){
            ansFin += grid[n/2][m/2];
        }
        int x = mp[1] , y =  mp[2];
        y = y % 2;
        if(y == 1) {
            if(x > 0){
                x--;
                ansFin++;
            }else {
                ansFin += 2;
            }
        }
        ansFin += x;
        return ansFin;
    }
};
