class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int m = grid.size();
        int n = grid[0].size();
        int cnt0 = 0;
        int cnt1 = 0;
        vector<vector<bool>> vis(m, vector<bool>(n));

        for(int i = 0; i < m /2; ++i){
            for(int j = 0; j < n/2; ++j){
                int cur = 0;
                if(grid[i][j])
                    ++cur;
                if(grid[m-i-1][j])
                    ++cur;
                if(grid[i][n-j-1])
                    ++cur;
                if(grid[m-i-1][n-j-1])
                    ++cur;
                grid[i][j] = grid[m-i-1][j] = grid[i][n-j-1] = grid[m-i-1][n-j-1] = true;
                ans += min(cur, 4-cur);
            }
        }
        int change = 0;

        if(m % 2){
            for(int j = 0; j < n/2; ++j){
                if(grid[m/2][j] != grid[m/2][n-j-1])
                    ++change;
                cnt1 += grid[m/2][j] + grid[m/2][n-j-1];
            }
        }
        if(n % 2){
            for(int i = 0; i < m/2; ++i){
                if(grid[i][n/2] != grid[m-i-1][n/2])
                    ++change;
                cnt1 += grid[i][n/2] + grid[m-i-1][n/2];
            }
        }
        if(m % 2 && n % 2 && grid[m/2][n/2])
            ++ans;

        cnt1 %= 4;

        ans += change;
        if(!change && cnt1)
            ans += 2;
        return ans;
    }
};
