class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int c22 = 0;
        int c13 = 0;
        int c31 = 0;
        int c11 = 0;
        int R = grid.size();
        int C = grid[0].size();
        // 不考虑中轴线时
        for (int i = 0; i < R / 2; i++) {
            int down = R - 1 - i;
            for (int j = 0; j < C / 2; j++) {
                int right = C - 1 - j;
                int sum = grid[i][j] + grid[i][right] + grid[down][j] + grid[down][right];
                if (sum == 1) {
                    c31++;
                } else if (sum == 2) {
                    c22++;
                } else if (sum == 3) {
                    c13++;
                }
            }
        }

        int onePair = 0;
        // 竖直中轴
        if (C % 2 == 1) {
            for (int i = 0, j = R - 1; i < j; i++, j--) {
                int a = grid[i][C / 2];
                int b = grid[j][C / 2];
                if (a == 1 && b == 1) onePair++;
                if (a != b) c11++;
            }
        }
        // 水平中轴
        if (R % 2 == 1) {
            for (int i = 0, j = C - 1; i < j; i++, j--) {
                int a = grid[R / 2][i];
                int b = grid[R / 2][j];
                if (a == 1 && b == 1) onePair++;
                if (a != b) c11++;
            }
        }
        // cout << "COUNT" << endl;
        // cout << "c31=" << c31 << endl;
        // cout << "c22=" << c22 << endl;
        // cout << "c13=" << c13 << endl;
        // cout << "c11=" << c11 << endl;
        int ans = c22 * 2 + c13 + c31 + c11;
        if (onePair & 1) {
            if (c11 == 0) ans += 2;
        }
        if ((R & 1) && (C & 1) && (grid[R / 2][C / 2])) {
            ans++;
        }
        return ans;
    }
};
