class Solution {
public:
    int func(vector<vector<int>>& arr, int i, int j, int n, int m)
    {
        int a = 0, b = 0;
        if(arr[i][j] == 0)a++;
        else b++;
        if(arr[i][m-1-j] == 0)a++;
        else b++;
        if(arr[n-1-i][j] == 0)a++;
        else b++;
        if(arr[n-1-i][m-1-j] == 0)a++;
        else b++;
        return min(a, b);
    }
    int minFlips(vector<vector<int>>& arr) {
        int n = arr.size();
        int m = arr[0].size();
        int cnt = 0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                cnt += func(arr, i, j, n, m);
            }
        }
        int x = 0;
        bool f = 0;
        if(m % 2 != 0)
        {
            for(int i=0;i<n/2;i++)
            {
                if(arr[i][m/2] != arr[n-1-i][m/2])
                {
                    cnt++;
                    f = 1;
                }
                if(arr[i][m/2] == 1)x++;
            }
        }
        // if(f == 0 && x % 2 == 1)cnt += 2;
        // x = 0;
        // f = 0;
        if(n % 2 != 0)
        {
            for(int i=0;i<m/2;i++)
            {
                if(arr[n/2][i] != arr[n/2][m-1-i])
                {
                    cnt++;
                    f = 1;
                }
                if(arr[n/2][i] == 1)x++;
            }

        }
        if(f == 0 && x % 2 == 1)cnt += 2;
        if(n % 2 != 0 && m % 2 != 0 && arr[n/2][m/2] == 1)cnt++;
        return cnt;
    }
};
